import { HttpClient } from '@angular/common/http';
import { Component, Inject, OnInit } from '@angular/core';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-base',
  templateUrl: './base.component.html',
  styleUrls: ['./base.component.css']
})
export class BaseComponent<TData> {
  protected data: TData[] = [];
  private baseUrl: string;

  constructor(private readonly http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    this.baseUrl = baseUrl;
  }

  protected async getData(controllerName: string): Observable<TData[]> {
    return this.http.get<TData[]>('https://localhost:7202/' + controllerName);
  }
}
